import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:flutter_screenutil/flutter_screenutil.dart';
import '../../../../core/di/injection.dart';
import '../../../../theming/colors.dart';
import '../../../../theming/text_styles.dart';
import '../bloc/search_bloc.dart';
import '../bloc/search_event.dart';
import '../bloc/search_state.dart';
import '../widgets/search_filters_widget.dart';
import '../widgets/property_card.dart';
import '../../data/models/property_search_request.dart';

class SearchScreen extends StatelessWidget {
  const SearchScreen({super.key});

  @override
  Widget build(BuildContext context) {
    return BlocProvider(
      create: (context) => getIt<SearchBloc>()..add(const LoadLookupsEvent()),
      child: const SearchScreenView(),
    );
  }
}

class SearchScreenView extends StatefulWidget {
  const SearchScreenView({super.key});

  @override
  State<SearchScreenView> createState() => _SearchScreenViewState();
}

class _SearchScreenViewState extends State<SearchScreenView>
    with TickerProviderStateMixin {
  late TabController _tabController;
  final ScrollController _scrollController = ScrollController();

  @override
  void initState() {
    super.initState();
    _tabController = TabController(length: 2, vsync: this);
  }

  @override
  void dispose() {
    _tabController.dispose();
    _scrollController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(
          'Property Search',
          style: AppTextStyles.h3.copyWith(color: AppColors.textPrimary),
        ),
        backgroundColor: Colors.white,
        elevation: 0,
        leading: IconButton(
          icon: const Icon(Icons.arrow_back, color: Colors.black87),
          onPressed: () => Navigator.of(context).pop(),
        ),
        bottom: TabBar(
          controller: _tabController,
          labelColor: AppColors.primary,
          unselectedLabelColor: AppColors.textSecondary,
          indicatorColor: AppColors.primary,
          tabs: const [
            Tab(text: 'Filters'),
            Tab(text: 'Results'),
          ],
        ),
      ),
      body: BlocConsumer<SearchBloc, SearchState>(
        listener: (context, state) {
          if (state is SearchError) {
            ScaffoldMessenger.of(context).showSnackBar(
              SnackBar(
                content: Text(state.message),
                backgroundColor: Colors.red,
              ),
            );
          }
        },
        builder: (context, state) {
          if (state is SearchInitial || state is LookupsLoading) {
            return const Center(
              child: CircularProgressIndicator(),
            );
          }

          if (state is LookupsLoaded || state is SearchSuccess || state is SearchError) {
            final propertyTypes = _getPropertyTypes(state);
            final propertyFeatures = _getPropertyFeatures(state);
            final governates = _getGovernates(state);
            final currentFilters = _getCurrentFilters(state);

            return TabBarView(
              controller: _tabController,
              children: [
                // Filters tab
                SingleChildScrollView(
                  padding: EdgeInsets.all(16.w),
                  child: Column(
                    children: [
                      SearchFiltersWidget(
                        propertyTypes: propertyTypes,
                        governates: governates,
                        initialFilters: currentFilters,
                        onFiltersChanged: (filters) {
                          context.read<SearchBloc>().add(UpdateSearchFiltersEvent(filters));
                        },
                      ),
                      SizedBox(height: 20.h),
                      SizedBox(
                        width: double.infinity,
                        child: ElevatedButton(
                          onPressed: () {
                            context.read<SearchBloc>().add(SearchPropertiesEvent(currentFilters));
                            _tabController.animateTo(1); // Switch to results tab
                          },
                          style: ElevatedButton.styleFrom(
                            backgroundColor: AppColors.primary,
                            foregroundColor: Colors.white,
                            padding: EdgeInsets.symmetric(vertical: 16.h),
                            shape: RoundedRectangleBorder(
                              borderRadius: BorderRadius.circular(8.r),
                            ),
                          ),
                          child: Text(
                            'Search Properties',
                            style: AppTextStyles.bodyLarge.copyWith(
                              color: Colors.white,
                              fontWeight: FontWeight.w600,
                            ),
                          ),
                        ),
                      ),
                    ],
                  ),
                ),

                // Results tab
                _buildResultsTab(state),
              ],
            );
          }

          return const Center(
            child: Text('Something went wrong'),
          );
        },
      ),
    );
  }

  Widget _buildResultsTab(SearchState state) {
    if (state is SearchLoading) {
      return const Center(
        child: CircularProgressIndicator(),
      );
    }

    if (state is SearchSuccess) {
      if (state.properties.isEmpty) {
        return Center(
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              Icon(
                Icons.search_off,
                size: 64.sp,
                color: AppColors.textSecondary,
              ),
              SizedBox(height: 16.h),
              Text(
                'No properties found',
                style: AppTextStyles.h3.copyWith(
                  color: AppColors.textSecondary,
                ),
              ),
              SizedBox(height: 8.h),
              Text(
                'Try adjusting your search filters',
                style: AppTextStyles.bodyMedium.copyWith(
                  color: AppColors.textSecondary,
                ),
              ),
            ],
          ),
        );
      }

      return Column(
        children: [
          // Results count header
          Container(
            width: double.infinity,
            padding: EdgeInsets.all(16.w),
            color: AppColors.backgroundColor,
            child: Text(
              '${state.totalCount} properties found',
              style: AppTextStyles.bodyMedium.copyWith(
                color: AppColors.textPrimary,  fontWeight: FontWeight.w500,
              ),
            ),
          ),

          // Properties list
          Expanded(
            child: ListView.builder(
              controller: _scrollController,
              itemCount: state.properties.length,
              itemBuilder: (context, index) {
                final property = state.properties[index];
                return PropertyCard(
                  property: property,
                  onTap: () {
                    // TODO: Navigate to property details
                    ScaffoldMessenger.of(context).showSnackBar(
                      SnackBar(
                        content: Text('Property: ${property.name ?? 'Unnamed'}'),
                      ),
                    );
                  },
                );
              },
            ),
          ),
        ],
      );
    }

    // Default state - show search prompt
    return Center(
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Icon(
            Icons.search,
            size: 64.sp,
            color: AppColors.textSecondary,
          ),
          SizedBox(height: 16.h),
          Text(
            'Search for Properties',
            style: AppTextStyles.h3.copyWith(
              color: AppColors.textSecondary,
            ),
          ),
          SizedBox(height: 8.h),
          Text(
            'Use filters to find your perfect property',
            style: AppTextStyles.bodyMedium.copyWith(
              color: AppColors.textSecondary,
            ),
          ),
          SizedBox(height: 24.h),
          ElevatedButton(
            onPressed: () {
              _tabController.animateTo(0); // Switch to filters tab
            },
            style: ElevatedButton.styleFrom(
              backgroundColor: AppColors.primary,
              foregroundColor: Colors.white,
              padding: EdgeInsets.symmetric(horizontal: 24.w, vertical: 12.h),
              shape: RoundedRectangleBorder(
                borderRadius: BorderRadius.circular(8.r),
              ),
            ),
            child: Text(
              'Set Filters',
              style: AppTextStyles.bodyMedium.copyWith(
                color: Colors.white,
                fontWeight: FontWeight.w600,
              ),
            ),
          ),
        ],
      ),
    );
  }

  List<LookupItem> _getPropertyTypes(SearchState state) {
    if (state is LookupsLoaded) return state.propertyTypes;
    if (state is SearchSuccess) return state.propertyTypes;
    if (state is SearchError) return state.propertyTypes;
    return [];
  }

  List<LookupItem> _getPropertyFeatures(SearchState state) {
    if (state is LookupsLoaded) return state.propertyFeatures;
    if (state is SearchSuccess) return state.propertyFeatures;
    if (state is SearchError) return state.propertyFeatures;
    return [];
  }

  List<LookupItem> _getGovernates(SearchState state) {
    if (state is LookupsLoaded) return state.governates;
    if (state is SearchSuccess) return state.governates;
    if (state is SearchError) return state.governates;
    return [];
  }

  PropertySearchRequest _getCurrentFilters(SearchState state) {
    if (state is LookupsLoaded) return state.currentFilters;
    if (state is SearchSuccess) return state.currentFilters;
    if (state is SearchError) return state.currentFilters;

    return const PropertySearchRequest(
      isActive: true,
      skipCount: 0,
      maxResultCount: 10,
    );
  }
}

