  SearchRepositoryImpl({
    required SearchRemoteDataSource remoteDataSource,
  }) : _remoteDataSource = remoteDataSource;

  @override
  Future<ApiResult<List<LookupItemEntity>>> getPropertyTypes() async {
    try {
      print('🔍 Fetching property types...');
      final response = await _remoteDataSource.getPropertyTypes();
      print('✅ Property types response: ${response.items.length} items');
      final entities = response.items
          .map((item) => LookupItemEntity(
                id: item.id,
                displayName: item.displayName,
              ))
          .toList();
      return ApiResult.success(entities);
    } catch (error) {
      print('❌ Property types error: $error');
      return ApiResult.failure(error.toString());
    }
  }

  @override
  Future<ApiResult<List<LookupItemEntity>>> getPropertyFeatures() async {
    try {
      final response = await _remoteDataSource.getPropertyFeatures();
      final entities = response.items
          .map((item) => LookupItemEntity(
                id: item.id,
                displayName: item.displayName,
              ))
          .toList();
      return ApiResult.success(entities);
    } catch (error) {
      return ApiResult.failure(error.toString());
    }
  }

  @override
  Future<ApiResult<List<LookupItemEntity>>> getGovernates() async {
    try {
      final response = await _remoteDataSource.getGovernates();
      final entities = response.items
          .map((item) => LookupItemEntity(
                id: item.id,
                displayName: item.displayName,
              ))
          .toList();
      return ApiResult.success(entities);
    } catch (error) {
      return ApiResult.failure(error.toString());
    }
  }

  @override
  Future<ApiResult<SearchResultEntity>> searchProperties(SearchFilter filter) async {
    try {
      final queryMap = SearchRemoteDataSourceHelper.filterToQueryMap(filter);
      print('🔍 Searching properties with filter: $queryMap');

      final response = await _remoteDataSource.searchProperties(queryMap);
      print('✅ Search response: ${response.items.length} properties found');

      final entities = response.items
          .map((property) => PropertyEntity(
                id: property.id,
                title: property.title,
                description: property.description,
                pricePerNight: property.pricePerNight,
                address: property.address,
                cityName: property.cityName,
                governateName: property.governateName,
                bedrooms: property.bedrooms,
                bathrooms: property.bathrooms,
                livingrooms: property.livingrooms,
                rating: property.rating,
                images: property.images,
                features: property.features,
                propertyTypeId: property.propertyTypeId,
                propertyTypeName: property.propertyTypeName,
                hotelName: property.hotelName,
                isActive: property.isActive,
              ))
          .toList();

      final searchResult = SearchResultEntity(
        properties: entities,
        totalCount: response.totalCount,
      );

      return ApiResult.success(searchResult);
    } catch (error) {
      print('❌ Search properties error: $error');
      return ApiResult.failure(error.toString());
    }
  }
}

import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:shared_preferences/shared_preferences.dart';
import 'dart:convert';
import '../../data/models/search_filter.dart';
import '../../domain/repositories/search_repository.dart';
import 'search_event.dart';
import 'search_state.dart';

class SearchBloc extends Bloc<SearchEvent, SearchState> {
  final SearchRepository _searchRepository;
  final SharedPreferences _sharedPreferences;
  static const String _recentSearchesKey = 'recent_searches';

  SearchBloc({
    required SearchRepository searchRepository,
    required SharedPreferences sharedPreferences,
  })  : _searchRepository = searchRepository,
        _sharedPreferences = sharedPreferences,
        super(const SearchInitial()) {
    on<LoadLookupsEvent>(_onLoadLookups);
    on<SearchPropertiesEvent>(_onSearchProperties);
    on<UpdateFilterEvent>(_onUpdateFilter);
    on<ResetFilterEvent>(_onResetFilter);
    on<LoadMorePropertiesEvent>(_onLoadMoreProperties);
    on<SaveRecentSearchEvent>(_onSaveRecentSearch);
  }

  Future<void> _onLoadLookups(
    LoadLookupsEvent event,
    Emitter<SearchState> emit,
  ) async {
    emit(const LookupsLoading());

    try {
      final results = await Future.wait([
        _searchRepository.getPropertyTypes(),
        _searchRepository.getPropertyFeatures(),
        _searchRepository.getGovernates(),
      ]);

      final propertyTypesResult = results[0];
      final propertyFeaturesResult = results[1];
      final governatesResult = results[2];

      propertyTypesResult.when(
        success: (propertyTypes) {
          propertyFeaturesResult.when(
            success: (propertyFeatures) {
              governatesResult.when(
                success: (governates) async {
                  final recentSearches = await _loadRecentSearches();
                  emit(LookupsLoaded(
                    propertyTypes: propertyTypes,
                    propertyFeatures: propertyFeatures,
                    governates: governates,
                    currentFilter: const SearchFilter(),
                    recentSearches: recentSearches,
                  ));
                },
                failure: (message) => emit(SearchError(message: message)),
              );
            },
            failure: (message) => emit(SearchError(message: message)),
          );
        },
        failure: (message) => emit(SearchError(message: message)),
      );
    } catch (error) {
      emit(SearchError(message: 'An unexpected error occurred: $error'));
    }
  }

  Future<void> _onSearchProperties(
    SearchPropertiesEvent event,
    Emitter<SearchState> emit,
  ) async {
    if (state is! LookupsLoaded && state is! SearchLoaded) {
      return;
    }

    emit(const SearchLoading());

    final result = await _searchRepository.searchProperties(event.filter);

    result.when(
      success: (searchResult) {
        final currentState = state;
        List<RecentSearch> recentSearches = [];

        if (currentState is LookupsLoaded) {
          recentSearches = currentState.recentSearches;
        } else if (currentState is SearchLoaded) {
          recentSearches = currentState.recentSearches;
        }

        final lookupsState = currentState is LookupsLoaded 
            ? currentState 
            : currentState is SearchLoaded 
                ? LookupsLoaded(
                    propertyTypes: currentState.propertyTypes,
                    propertyFeatures: currentState.propertyFeatures,
                    governates: currentState.governates,
                    currentFilter: event.filter,
                    recentSearches: recentSearches,
                  )
                : null;

        if (lookupsState != null) {
          emit(SearchLoaded(
            properties: searchResult.properties,
            totalCount: searchResult.totalCount,
            currentFilter: event.filter,
            propertyTypes: lookupsState.propertyTypes,
            propertyFeatures: lookupsState.propertyFeatures,
            governates: lookupsState.governates,
            recentSearches: recentSearches,
            hasReachedMax: searchResult.properties.length < event.filter.maxResultCount,
          ));
        }
      },
      failure: (message) => emit(SearchError(message: message)),
    );
  }

  Future<void> _onUpdateFilter(
    UpdateFilterEvent event,
    Emitter<SearchState> emit,
  ) async {
    if (state is LookupsLoaded) {
      final currentState = state as LookupsLoaded;
      emit(LookupsLoaded(
        propertyTypes: currentState.propertyTypes,
        propertyFeatures: currentState.propertyFeatures,
        governates: currentState.governates,
        currentFilter: event.filter,
        recentSearches: currentState.recentSearches,
      ));
    } else if (state is SearchLoaded) {
      final currentState = state as SearchLoaded;
      emit(currentState.copyWith(currentFilter: event.filter));
    }
  }

  Future<void> _onResetFilter(
    ResetFilterEvent event,
    Emitter<SearchState> emit,
  ) async {
    if (state is LookupsLoaded) {
      final currentState = state as LookupsLoaded;
      emit(LookupsLoaded(
        propertyTypes: currentState.propertyTypes,
        propertyFeatures: currentState.propertyFeatures,
        governates: currentState.governates,
        currentFilter: const SearchFilter(),
        recentSearches: currentState.recentSearches,
      ));
    } else if (state is SearchLoaded) {
      final currentState = state as SearchLoaded;
      emit(currentState.copyWith(currentFilter: const SearchFilter()));
    }
  }

  Future<void> _onLoadMoreProperties(
    LoadMorePropertiesEvent event,
    Emitter<SearchState> emit,
  ) async {
    if (state is! SearchLoaded) return;

    final currentState = state as SearchLoaded;
    if (currentState.hasReachedMax) return;

    final newFilter = currentState.currentFilter.copyWith(
      skipCount: currentState.properties.length,
    );

    final result = await _searchRepository.searchProperties(newFilter);

    result.when(
      success: (searchResult) {
        final newProperties = [...currentState.properties, ...searchResult.properties];

        emit(currentState.copyWith(
          properties: newProperties,
          totalCount: searchResult.totalCount,
          hasReachedMax: searchResult.properties.length < newFilter.maxResultCount,
        ));
      },
      failure: (message) {
        // Handle error for load more if needed
      },
    );
  }

  Future<void> _onSaveRecentSearch(
    SaveRecentSearchEvent event,
    Emitter<SearchState> emit,
  ) async {
    final recentSearches = await _loadRecentSearches();

    final newSearch = RecentSearch(
      location: event.location,
      checkIn: event.checkIn,
      checkOut: event.checkOut,
      guests: event.guests,
      timestamp: DateTime.now(),
    );

    // Remove duplicate if exists and add to beginning
    final updatedSearches = [
      newSearch,
      ...recentSearches.where((search) => 
          search.location != event.location ||
          search.checkIn != event.checkIn ||
          search.checkOut != event.checkOut ||
          search.guests != event.guests)
    ].take(5).toList(); // Keep only 5 recent searches

    await _saveRecentSearches(updatedSearches);

    // Update current state with new recent searches
    if (state is LookupsLoaded) {
      final currentState = state as LookupsLoaded;
      emit(LookupsLoaded(
        propertyTypes: currentState.propertyTypes,
        propertyFeatures: currentState.propertyFeatures,
        governates: currentState.governates,
        currentFilter: currentState.currentFilter,
        recentSearches: updatedSearches,
      ));
    } else if (state is SearchLoaded) {
      final currentState = state as SearchLoaded;
      emit(currentState.copyWith(recentSearches: updatedSearches));
    }
  }

  Future<List<RecentSearch>> _loadRecentSearches() async {
    final jsonString = _sharedPreferences.getString(_recentSearchesKey);
    if (jsonString == null) return [];

    try {
      final List<dynamic> jsonList = json.decode(jsonString);
      return jsonList.map((json) => RecentSearch(
        location: json['location'],
        checkIn: DateTime.parse(json['checkIn']),
        checkOut: DateTime.parse(json['checkOut']),
        guests: json['guests'],
        timestamp: DateTime.parse(json['timestamp']),
      )).toList();
    } catch (e) {
      return [];
    }
  }

  Future<void> _saveRecentSearches(List<RecentSearch> searches) async {
    final jsonList = searches.map((search) => {
      'location': search.location,
      'checkIn': search.checkIn.toIso8601String(),
      'checkOut': search.checkOut.toIso8601String(),
      'guests': search.guests,
      'timestamp': search.timestamp.toIso8601String(),
    }).toList();

    await _sharedPreferences.setString(_recentSearchesKey, json.encode(jsonList));
  }
}

